import React, {Component} from 'react';

class Counter extends Component {
    state = {
        value: this.props.value,
        tags: ['tag1', 'tag2', 'tag3'],
        id:0
    };

    styles = {
        fontSize: 15,
        fontWeight: 'bold'
    };

    // renderTags() {
    //     if (this.state.tags.length === 0) return <p>There are no Tags!</p>;
    //     return (<ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul>);
    // }

    handleIncrement = (product) =>{
        this.setState({ value: this.state.value + 1 });
    }


    render() {

        return (
            // Can use <React.Fragment> instead of Div if you dont want to create another div
            <div>
                <span style={this.styles} className={this.getBtnClasses()}>{this.formatCount()}</span>
                <button onClick={() => this.handleIncrement(this.key)} className={'btn btn-secondary btn-sm'}>Increment</button>
                <button onClick={() => this.props.onDelete(this.props.id)} className="btn btn-danger btn-sm m-2">Delete</button>
                {/*{this.state.tags.length===0 && 'Please create a new Tag!'}*/}
                {/*{this.renderTags()}*/}
            </div>
        );
    }


    getBtnClasses() {
        let btnClasses = 'badge m-2 badge-';
        btnClasses += (this.state.value === 0 ? 'warning' : 'primary');
        return btnClasses;
    }

    formatCount(){
        const { value } = this.state;
        return value === 0 ? 'Zero' : value;
    }
}
export default Counter;
