import React, {Component} from "react";
import Counter from "./counterComponent";

class Counters extends Component {
    state={
        counters: [
            {id:1,value:0},
            {id:2,value:2},
            {id:3,value:0},
            {id:4,value:0}
        ]
    }
    render(){
        return(
            <div>
                {this.state.counters.map(counter=>
                    <Counter key={Counter.id} value={counter.value}>
                        <h4>Counter</h4>
                    </Counter>
                )}
            </div>
        );
    }
}

export default Counters;
