import React, {useState} from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import AuthApi from "./AuthApi";
import { useCookies } from 'react-cookie';
import Salt from "./hashhash";

const useStyles = makeStyles(theme => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: 250,
        },
    },
}));

export default function Login() {
    const [cookies, setCookie, removeCookie] = useCookies(['user']);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const Auth = React.useContext(AuthApi);
    const salt = React.useContext(Salt);

    const handleOnClick = async () => {
        hashThePassword(password, salt);
        const url = "http://localhost/errorkb/api/user?username=" + username;
        const response = await fetch(url);
        const data = await response.json();
        if (data.username == username && data.passwd == password) {
            Auth.setAuth(true);
            setCookie("user", data.username);
            setCookie("rights", data.privileges);
        } else {
            alert("Wrong credentials")
        }
    }


    var crypto = require('crypto');
    var hashThePassword = function (password, salt) {
        password =
            password.length && typeof password === 'string' ? password : undefined;
        if (password && salt) {
            // Create the salt
            let hash = crypto
                .createHmac('sha512', salt)
                .update(password)
                .digest('hex');
            // Return a salt and hash object
            console.log(salt + "" + hash);
            console.log(password);
            return {salt, hash};
        } else {
            return null;
        }

        const classes = useStyles();

        return (

            <form className={classes.root} noValidate autoComplete="off">

                <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    marginTop: '100px',
                }}>
                    <Grid container spacing={4}>
                        <Grid item xs={5} sm={5}/>
                        <Grid item xs={9} sm={5}>
                            <TextField
                                id="standard-username-input"
                                label="Username"
                                type="text"
                                autoComplete="current-username"
                                value={username}
                                onChange={
                                    (e) => {
                                        setUsername(e.target.value);
                                    }}
                            />
                        </Grid>

                    </Grid>
                </div>
                <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                }}>
                    <Grid container spacing={4}>
                        <Grid item xs={5} sm={5}/>
                        <Grid item xs={9} sm={5}>
                            <TextField
                                id="standard-password-input"
                                label="Password"
                                type="password"
                                autoComplete="current-password"
                                value={password}
                                onChange={
                                    (e) => {
                                        setPassword(e.target.value)
                                    }}
                            />
                            <Grid item xs={5}/>
                        </Grid>
                    </Grid>

                </div>

                <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    paddingTop: '10px',
                    paddingLeft: '10px',
                }}>
                    <Grid container spacing={4}>
                        <Grid item xs={5} sm={5}/>

                        <Grid item xs={8} sm={6}>
                            <Button onClick={handleOnClick} className={classes.root} style={{width: '255px'}}
                                    variant="outlined" color="Primary">
                                Login
                            </Button>
                            <Grid item xs={5}/>
                        </Grid>
                    </Grid>

                </div>
            </form>
        );
    }
}
