//         btnClasses += (this.state.count === 0 ? 'warning' : 'primary');
//         return btnClasses;
//     }
//
//     formatCount(){
//         const { count } = this.state;
//         return count === 0 ? 'Zero' : count;
//     }
// }
}
export default Counter;
